import React, { Component } from "react";
import { Image, Button } from "react-bootstrap";
import axios from "axios";
import Image1 from "../../assets/burger.jpeg";
// import Image2 from "../../assets/coke.jpeg";
// import Image3 from "../../assets/fries.jpeg";
// import Image4 from "../../assets/pepsi.jpeg";

class About extends Component {
  constructor() {
    super();
    this.state = {
      list: null,
      name: null,
      image: null,
      price: null,
    };
  }
  componentDidMount() {
    axios.get("http://localhost:3000/restaurant").then((Res) => {
      Res.data.map((item, index) => {
        item.qty = 1;
        return item;
      });
      this.setState({ list: Res.data });
    });
  }

  AddPrice(index, price) {
    console.log(index);
    const { list } = this.state;
    const newprice = Number(list[index].qty) + 1;
    list[index].qty = newprice;
    this.setState({
      ...this.state,
      list: list,
    });
  }

  RemovePrice(index, price) {
    console.log(index);
    const { list } = this.state;
    const newprice = Number(list[index].qty) - 1;
    list[index].qty = newprice;
    this.setState({
      ...this.state,
      list: list,
    });
  }

  render() {
    return (
      <div>
        <section id="values" className="values">
          <div className="container" data-aos="fade-up">
            {this.state.list ? (
              <div
                style={{
                  display: "flex",
                  justifyContent: "space-between",
                }}
              >
                {this.state.list.map((item, i) => (
                  <div
                    className="row"
                    style={{
                      margin: "2%",
                      padding: "5% 5%",
                      background: " beige",
                    }}
                  >
                    {Array.from({ length: 1 }).map((_, index) => (
                      <div className="col-lg-3" key={index}>
                        <div
                          className="box"
                          data-aos="fade-up"
                          data-aos-delay="200"
                          style={{
                            display: "inline-block",
                          }}
                        >
                          if ({index===0}) {
                            <Image src={Image1} rounded style={Imagesty} />
                          }
                          {/* if (i==1) {
                          <Image src={Image2} rounded style={Imagesty} />
                          }
                          if (i==2) {
                            <Image src={Image3} rounded style={Imagesty} />
                          }
                          if (i==3) {
                          <Image src={Image4} rounded style={Imagesty} />
                          } */}
                          <h2>{item.name}</h2>
                          <p>Price {item.price * item.qty}</p>
                          <Button
                            variant="primary"
                            onClick={() => {
                              this.AddPrice(i, item.price);
                            }}
                          >
                            +
                          </Button>{" "}
                          <Button
                            variant="secondary"
                            onClick={() => {
                              this.RemovePrice(i, item.price);
                            }}
                          >
                            -
                          </Button>{" "}
                        </div>
                      </div>
                    ))}
                  </div>
                ))}
              </div>
            ) : (
              <p>please wait....</p>
            )}
          </div>
        </section>
      </div>
    );
  }
}
const Imagesty = {
  width: "100%",
};

export default About;
