import React, { Component } from "react";
import { Image, Button } from "react-bootstrap";
import axios from "axios";
import Imsge from "../../assets/burger.jpeg"
// import Imsge from "../../assets/coke.jpeg"
// import Imsge from "../../assets/fries.jpeg"
// import Imsge from "../../assets/pepsi.jpeg"

class About extends Component {
  constructor() {
    super();
    this.state = {
      list: null,
      name: null,
      image: null,
      price: null,
    };
  }
  componentDidMount() {
    axios.get("http://localhost:3000/restaurant").then((Res) => {
      this.setState({ list: Res.data });
    });
  }

  update(index,price){
   const{list}=this.state
   const newprice=Number(list[index].price)+Number(price)
   list[index].price=newprice
   this.setState({
     ...this.state,
     list:list
   })
  }
  render() {
    return (
      <div>
        <section id="values" className="values">
          <div className="container" data-aos="fade-up">
            {this.state.list ? (
              <div
                style={{
                  display: "flex",
                  justifyContent: "space-between",
                }}
              >
                {this.state.list.map((item, i) => (
                  <div
                    className="row"
                    style={{
                      margin: "2%",
                      padding: "5% 5%",
                      background: " beige",
                    }}
                  >
                    {Array.from({ length: 1 }).map((_, index) => (
                      <div className="col-lg-3">
                        <div
                          className="box"
                          data-aos="fade-up"
                          data-aos-delay="200"
                          style={{
                            display: "inline-block",
                          }}
                        >
                          <img src={Imsge} rounded style={Imagesty} />
                          {/* {item.image} */}
                          <h2>{item.name}</h2>
                          <p>Price {item.price}</p>
                          <Button
                            variant="primary"
                            onClick={() => {
                              // this.setState({
                              //   price: item.price.value + item.price.value,
                              // });
                              this.update(index,item.price)
                            }}
                          >
                            +
                          </Button>{" "}
                          <Button variant="secondary" onclick={() => {
                              this.setState({
                                price: item.price.value + item.price.value,
                              });
                            }}>-</Button>{" "}
                        </div>
                      </div>
                    ))}
                  </div>
                ))}
              </div>
            ) : (
              <p>please wait....</p>
            )}
          </div>
        </section>
      </div>
    );
  }
}
const Imagesty = {
  width: "100%",
};

export default About;
